import { Component } from '@angular/core';
import { Cliente } from 'src/app/models/cliente';
import { ClienteService } from 'src/app/service/cliente.service';

@Component({
  selector: 'app-search-budget',
  templateUrl: './search-budget.component.html',
  styleUrls: ['./search-budget.component.css']
})
export class SearchBudgetComponent {
  clientes?: Cliente[];
  currentCliente: Cliente = {};
  currentIndex = -1;
  producto = '';

  constructor(private clienteService: ClienteService) {}

  ngOnInit(): void {
    this.listarClientes();
  }


  listarClientes(): void {

    this.currentCliente = {};
    this.currentIndex = -1;

    this.clienteService.getAll().subscribe({
      next: (data) => {
        this.clientes = data;
        console.log(data);
      },
      error: (e) => console.error(e)
    });
  }

  seleccionarCliente(clienteId: string): void {
    console.log("Cliente seleccionado (ID):", clienteId);

    if (!clienteId) {
      this.currentCliente = {} as Cliente; // Resetea si no hay selección
      return;
    }

    const clienteEncontrado = this.clientes?.find(cliente => cliente.id?.toString() === clienteId);

    if (clienteEncontrado) {
      this.currentCliente = clienteEncontrado;
    } else {
      this.currentCliente = {} as Cliente;
    }

    console.log("Cliente seleccionado:", this.currentCliente);
  }



}
